function convert(e){var n=1024*(1<<30),t=1<<30,r=1<<20,i=1024,a=Math.abs(e);return a>=n?Math.round(e/n*100)/100+"tb":a>=t?Math.round(e/t*100)/100+"gb":a>=r?Math.round(e/r*100)/100+"mb":a>=i?Math.round(e/i*100)/100+"kb":e+"b"}module.exports=function(e){if("number"==typeof e)return convert(e);var n=e.match(/^(\d+(?:\.\d+)?) *(kb|mb|gb|tb)$/),t=parseFloat(n[1]),r=n[2],i={kb:1024,mb:1<<20,gb:1<<30,tb:1024*(1<<30)};return i[r]*t};